import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { deleteUser, getAllUsers } from '../../redux/apiRequest';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { loginSuccess } from '../../redux/authSlice';

function Home() {
    const user = useSelector((state) => state.auth.login?.currentUser);
    const userList = useSelector((state) => state.users.users?.allUsers);
    const dispatch = useDispatch();
    let axiosJWT = axios.create();

    useEffect(() => {
        getAllUsers(user?.accessToken, dispatch);
    }, []);

    const handleDelete = (id) => {
        deleteUser(id, user?.accessToken, dispatch);
    };

    const refreshToken = async () => {
        try {
            const res = await axios.post('http://localhost:8000/v1/auth/refresh', {
                withCredentials: true,
            });
            return res.data;
        } catch (err) {
            console.log(err);
        }
    };

    axios.interceptors.request.use(async (config) => {
        let date = new Date();
        const decodeToken = jwt_decode(user?.accessToken);
        if (decodeToken.exp < date.getTime() / 1000) {
            const data = await refreshToken();
            const refreshUser = {
                ...user,
                accessToken: data.accessToken,
            };
            dispatch(loginSuccess(refreshUser));
        }
    });
    return (
        <main className="container d-flex">
            {userList?.map((data, index) => (
                <Card className="mt-5 me-5 ms-5" style={{ width: '18rem' }} key={index}>
                    <Card.Body>
                        <Card.Title>{data.username}</Card.Title>
                        <Card.Text>{data.email}</Card.Text>
                        <Card.Text>{data.admin ? <>admin</> : <>user</>}</Card.Text>
                        <Button variant="primary">Detail</Button>
                        <Button variant="outline-primary ms-5" onClick={() => handleDelete(user._id)}>
                            Delete
                        </Button>
                    </Card.Body>
                </Card>
            ))}
        </main>
    );
}

export default Home;
